package dataType;

public class VariablesInteger {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int variable1 = 9;
		int variable2, variable3;
		
		variable2 = 20;
		variable3 = 25;
		
		System.out.println("Variable 1 is " + variable1);
		System.out.println("Variable 2 is " + variable2 +", Variable 3 is "+ variable3);
		System.out.println(variable1+variable2);
		System.out.println(variable1*variable3);
		System.out.println(variable3/variable2);
		System.out.println(variable3-variable2);
		
		
	}

}
