package learnJava;

public class LearnArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String[] names = new String[3];
		
		names[0] = "Mamun";
		names[1] = "touhid";
		names[2] = "Hasnath";
		
		for (String eachname : names) {
			
			System.out.println(eachname);
			
		}

	}

}
